import React, { Component } from "react";
import io from "socket.io-client";

let socket;
export class SocketIOTest extends Component {
  componentDidMount() {
    socket = io("http:localhost:5000/");
    socket.emit(
      "todoCreated",
      { user: "Ravinder", action: "Todo created." },
      (error) => {
        if (error) {
          alert(error);
        }
      }
    );
  }
  render() {
    return (
      <div>
        <input type="button" onClick={this.handleSubmit}>
          Submit
        </input>
      </div>
    );
  }
}

export default SocketIOTest;
