import React, { Component } from "react";
import io from "socket.io-client";

let socket;
export class SocketIOTest extends Component {
  componentDidMount() {
    socket = io("http://localhost:5000/");
  }
  handleSubmit = () => {
    socket.emit(
      "todoCreated",
      { user: "Ravinder", action: "Todo created." },
      (error) => {
        if (error) {
          alert(error);
        }
      }
    );
  };
  render() {
    return (
      <div>
        <button type="button" onClick={this.handleSubmit}>
          Submit
        </button>
      </div>
    );
  }
}

export default SocketIOTest;
